targets.expr:
max by (namespace, pod, container) (
  rate(container_cpu_usage_seconds_total{namespace="elastic-$esCluster",pod=~"$esNode",container!=""}[2m])
) /
(min by (namespace, pod, container) (
  kube_pod_container_resource_limits{
    namespace="elastic-$esCluster",
    resource="cpu"
  }) or min by (namespace, pod, container) (
  kube_pod_container_resource_requests{
    namespace="elastic-$esCluster",
    resource="cpu"
  }))

max by (namespace, pod, container) (
  container_memory_working_set_bytes{
    namespace="elastic-$esCluster",
    pod=~"$esNode",
    container!=""
  }
) /
(min by (namespace, pod, container) (
  kube_pod_container_resource_limits{
    namespace="elastic-$esCluster",
    resource="memory"
  }) or min by (namespace, pod, container) (
  kube_pod_container_resource_requests{
    namespace="elastic-$esCluster",
    resource="memory"
  }))
